import osos.environ['NLS_LANG'] = 'SIMPLIFIED CHINESE_CHINA.UTF-8'import pytesseractfrom PIL import Imageimport PIL.ImageOpsclass dy_score:    def __init__(self,path):        self.info = {            "format_size":(1600,1200),            "score_loc":(1030,400,1480,490),            "perf_loc":(880,630,1000,670),            "good_loc":(880,680,1000,720),            "miss_loc":(880,730,1000,770),            "combo_loc":(880,780,1000,820)            }        self.load_pic(path)        self.pic = {            "score":self.format_cut(self.info["score_loc"]),            "perfect":self.format_cut(self.info["perf_loc"]),            "good":self.format_cut(self.info["good_loc"]),            "miss":self.format_cut(self.info["miss_loc"]),            "combo":self.format_cut(self.info["combo_loc"])            }        self.data = {            "score":self.img2str(self.pic["score"]),            "perfect":self.img2str(self.pic["perfect"]),            "good":self.img2str(self.pic["good"]),            "miss":self.img2str(self.pic["miss"]),            "combo":self.img2str(self.pic["combo"])            }        print(self.data)    def img2str(self,img):                ret = pytesseract.image_to_string(PIL.ImageOps.invert(img),lang='eng',\                               config='result -psm 10 digits')        try:            ret = int(ret)        except:            print("error deal with string : %s"%ret)            ret = None        return ret    def load_pic(self,path):        self.img = Image.open(path)        print(path,end="\t")        print(self.img.size)    def format_cut(self,box):        ret = self.img.resize(self.info["format_size"]).crop(box)        return ret    sc = dy_score("./src_pic/4.jpg")